class BlurryImageLoad{constructor(){this.images=[]}load(){this.images=document.querySelectorAll(".blurry-load:not(.loaded)");for(let e=0;e<this.images.length;e++){const t=this.images[e];t.hasAttribute("data-large")&&t.setAttribute("data-placeholder",t.src);const a=new Image;a.src=t.getAttribute("data-large")||t.src,a.onload=()=>{t.src=a.src,t.classList.add("loaded")}}}}window.BlurryImageLoad=BlurryImageLoad; 